html,
body {
  padding: 0;
  margin: 0;
}

html {
  min-height: 100vh;
}

body {
  font-family: serif;
  min-height: 100vh;
  font-size: 62.5%;
  line-height: 1.6;
  text-align: center;
}

h1,
h2,
h3,
h4,
p,
figure,
figcaption {
  margin: 0;
  padding: 0;
}

h1,
h2,
h3 {
  font-family: sans-serif;
}

h1 {
  text-transform: capitalize;
  letter-spacing: 1px;
}

.header {
  display: grid;
  grid-template-columns: max-content 1fr max-content;
  overflow: hidden;
}
.header__logo {
  grid-column: 1/2;
  border-right: 1px solid #F6F6F6;
  display: flex;
  padding: 5px;
}
.header__logo img {
  width: 75px;
  align-self: center;
}
.header__cart {
  grid-column: 3/-1;
  background: #F6F6F6;
  width: 12rem;
  text-decoration: none;
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1rem;
  text-transform: capitalize;
  color: #2C2C2C;
  font-family: sans-serif;
  transition: all 0.2s ease;
}
.header__cart p {
  padding-right: 1rem;
}
.header__cart svg {
  fill: #B0B0B0;
  border: red;
}
.header__cart:hover {
  background: #dddddd;
}
.header__cart:hover svg {
  fill: #fff;
}

.header__nav {
  grid-column: 2/3;
  display: grid;
  grid-auto-flow: column;
  align-items: center;
  justify-content: center;
  grid-column-gap: 2.5rem;
}
.header__nav input[type=radio] {
  display: none;
}
.header__nav label {
  cursor: pointer;
  transition: all 0.2s ease;
}
.header__nav label:hover {
  transform: scale(1.1);
}
.header__nav a {
  text-decoration: none;
  color: #1F1F1F;
  font-size: 0.9rem;
  text-transform: capitalize;
  font-family: sans-serif;
  position: relative;
  display: flex;
  align-items: end;
  justify-content: center;
}
.header__nav a:after {
  content: "";
  display: none;
  background-image: url("6ed202056e2b34e420da56c8009cf388.svg");
  width: 18px;
  height: 18px;
  background-size: 18px 18px;
  padding: 0;
  margin: 0;
  background-repeat: no-repeat;
  position: absolute;
  bottom: -20px;
}
.header__nav input:checked + a:after {
  display: block;
}
.header__nav input:checked + a label {
  transform: scale(1.1);
}

html {
  box-sizing: border-box;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

img {
  max-width: 100%;
  height: auto;
}

body {
  display: grid;
}

.section--carousel {
  display: grid;
  height: 80vh;
  grid-template-columns: 2fr 1fr;
  grid-template-rows: 1fr 80px;
}

.carousel__img {
  grid-column: 1/2;
  grid-row: 1/3;
  width: 100%;
  height: 100%;
  object-fit: cover;
  align-self: start;
}
.carousel__info {
  grid-column: 2/-1;
  display: grid;
  grid-template-columns: 30px 1fr 50px;
  grid-template-rows: 80px 1fr 20px 1fr 1fr;
  background: #191919;
  align-items: center;
}
.carousel__info h1 {
  color: #F6F6F6;
  grid-column: 2/3;
  grid-row: 2/3;
  font-size: 2rem;
  width: 90%;
  text-align: left;
}
.carousel__info h3, .carousel__info p {
  color: #F6F6F6;
}
.carousel__info h3 {
  grid-row: 1/2;
  grid-column: 2/3;
  align-self: end;
  text-transform: uppercase;
  letter-spacing: 5px;
  font-size: 1.3rem;
  font-family: sans-serif;
  font-weight: 300;
  text-align: left;
  color: #B0B0B0;
}
.carousel__info p {
  grid-row: 4/5;
  grid-column: 2/3;
  line-height: 1.8;
  font-size: 0.9rem;
  text-align: left;
  font-family: sans-serif;
  width: 80%;
}
.carousel__info .read-more {
  grid-row: 5/6;
  grid-column: 2/3;
  color: #fff;
  text-decoration: none;
  text-align: left;
  font-size: 1.3rem;
  display: flex;
  align-items: center;
  text-transform: capitalize;
  font-family: sans-serif;
  padding: 15px 2px;
  max-width: 250px;
  width: 100%;
}
.carousel__info .read-more:hover svg {
  margin-left: 2rem;
}
.carousel__info .read-more svg {
  fill: #fff;
  margin-left: 1rem;
  width: 20px;
  transition: all 0.1s ease;
}
.carousel__controls {
  grid-column: 2/3;
  grid-row: 2/3;
  display: grid;
  grid-template-columns: 1fr 1fr;
}
.carousel__controls label {
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.3rem;
  text-transform: capitalize;
  color: #A3A3A3;
  cursor: pointer;
}
.carousel__controls label p {
  padding: 0 10px;
}
.carousel__controls label svg {
  width: 20px;
  height: 20px;
  fill: #B0B0B0;
}
.carousel__controls label:first-of-type {
  border: 3px solid #F6F6F6;
}
.carousel__controls label:last-of-type {
  border-bottom: 3px solid #F6F6F6;
}
