.card {
  display: grid;
  width: 100%;
  justify-content: center;
  justify-items: center;
  grid-template-rows: auto auto;
  height: 100%;
}
.card img {
  width: 100%;
  object-fit: cover;
}
.card__info {
  display: grid;
  grid-template-columns: min-content 1fr;
  grid-template-rows: repeat(2, auto);
  width: 100%;
  grid-row-gap: 10px;
  grid-column-gap: 1rem;
  padding-top: 1rem;
}
.card__camera {
  grid-column: 1/2;
  grid-row: 1/2;
  justify-self: start;
  width: 30px;
  height: 25px;
  fill: none;
  stroke: #B0B0B0;
  stroke-width: 1px;
}
.card__date {
  grid-column: 2/3;
  grid-row: 1/2;
  display: flex;
  align-items: center;
  justify-content: start;
  font-size: 1.1rem;
  color: #8E8E8E;
  font-weight: 900;
  letter-spacing: 1px;
}
.card a {
  grid-column: 1/-1;
  grid-row: 2/3;
}
.card--big {
  width: 100%;
  display: grid;
  grid-template-rows: auto;
  position: relative;
  height: 450px;
}
.card--big img {
  object-fit: cover;
  grid-column: 1/-1;
  grid-row: 1/2;
  align-self: start;
  width: 100%;
  height: 100%;
}
.card--big h2 {
  grid-row: 2/3;
  grid-column: 2/3;
  color: #fff;
}
.card--big h1 {
  grid-row: 2/3;
}
.card--big__info {
  grid-column: 1/-1;
  grid-row: 1/2;
  z-index: 15;
  display: grid;
  grid-template-rows: auto auto auto 20px;
  grid-template-columns: 50px 1fr 1fr;
  align-content: end;
  align-items: center;
  justify-items: start;
  color: #F6F6F6;
  background: rgba(0, 0, 0, 0.2);
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  top: 0;
  grid-row-gap: 15px;
}
.card--big__info h1 {
  grid-row: 3/4;
  grid-column: 2/3;
  font-family: serif;
  font-size: 4rem;
  align-self: start;
}
.card--big .btn--arrow {
  grid-column: 3/-1;
  grid-row: 3/4;
}
.card--big__video-svg {
  grid-column: 2/3;
  grid-row: 1/2;
  fill: none;
  stroke: #F6F6F6;
  stroke-width: 2px;
  width: 50px;
  height: 50px;
}
.card--big-simple .btn--arrow {
  grid-column: 2/3;
  justify-content: start;
}

