generated.css:
.align-r {
  justify-content: end;
  text-align: right;
}

.align-l {
  justify-content: start;
  text-align: left;
}

.align-c {
  justify-content: center;
  text-align: center;
}

html,
body {
  padding: 0;
  margin: 0;
}

html {
  min-height: 100vh;
}

body {
  font-family: serif;
  min-height: 100vh;
  font-size: 62.5%;
  line-height: 1.6;
  text-align: center;
}

h1,
h2,
h3,
h4,
p,
figure,
figcaption {
  margin: 0;
  padding: 0;
}

h1,
h2,
h3 {
  font-family: sans-serif;
}

h1 {
  text-transform: capitalize;
  letter-spacing: 1px;
  font-size: 4rem;
  color: #F6F6F6;
  font-family: serif;
  text-align: left;
}

h2 {
  text-align: left;
  font-size: 1.2rem;
  letter-spacing: 8px;
  color: #8E8E8E;
  font-weight: 300;
  word-spacing: 0.5rem;
  text-transform: uppercase;
}

h3 {
  text-transform: capitalize;
  letter-spacing: 2px;
  font-family: sans-serif;
  align-self: center;
  justify-self: start;
  font-size: 2.5rem;
  font-weight: 900;
  color: #2C2C2C;
}

.header {
  display: grid;
  grid-template-columns: max-content 1fr max-content;
  overflow: hidden;
}
.header__logo {
  grid-column: 1/2;
  border-right: 1px solid #F6F6F6;
  display: flex;
  padding: 5px;
}
.header__logo img {
  width: 75px;
  align-self: center;
}
.header__cart {
  grid-column: 3/-1;
  background: #F6F6F6;
  width: 12rem;
  text-decoration: none;
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1rem;
  text-transform: capitalize;
  color: #2C2C2C;
  font-family: sans-serif;
  transition: all 0.2s ease;
}
.header__cart p {
  padding-right: 1rem;
}
.header__cart svg {
  fill: #B0B0B0;
  border: red;
}
.header__cart:hover {
  background: #dddddd;
}
.header__cart:hover svg {
  fill: #fff;
}

.header__nav {
  grid-column: 2/3;
  display: grid;
  grid-auto-flow: column;
  align-items: center;
  justify-content: center;
  grid-column-gap: 2.5rem;
}
.header__nav input[type=radio] {
  display: none;
}
.header__nav label {
  cursor: pointer;
  transition: all 0.2s ease;
}
.header__nav label:hover {
  transform: scale(1.1);
}
.header__nav a {
  text-decoration: none;
  color: #1F1F1F;
  font-size: 0.9rem;
  text-transform: capitalize;
  font-family: sans-serif;
  position: relative;
  display: flex;
  align-items: end;
  justify-content: center;
}
.header__nav a:after {
  content: "";
  display: none;
  background-image: url("6ed202056e2b34e420da56c8009cf388.svg");
  width: 18px;
  height: 18px;
  background-size: 18px 18px;
  padding: 0;
  margin: 0;
  background-repeat: no-repeat;
  position: absolute;
  bottom: -20px;
}
.header__nav input:checked + a:after {
  display: block;
}
.header__nav input:checked + a label {
  transform: scale(1.1);
}

html {
  box-sizing: border-box;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

img {
  max-width: 100%;
  height: auto;
}

body {
  display: grid;
}

.section--carousel {
  display: grid;
  height: 80vh;
  grid-template-columns: 2fr 1fr;
  grid-template-rows: 1fr 80px;
}

.carousel__img {
  grid-column: 1/2;
  grid-row: 1/3;
  width: 100%;
  height: 100%;
  object-fit: cover;
  align-self: start;
}
.carousel__info {
  grid-column: 2/-1;
  display: grid;
  grid-template-columns: 30px 1fr 50px;
  grid-template-rows: 80px 1fr 20px 1fr 1fr;
  background: #191919;
  align-items: center;
}
.carousel__info h1 {
  grid-column: 2/3;
  grid-row: 2/3;
  font-size: 2rem;
  width: 90%;
  text-align: left;
}
.carousel__info p {
  color: #F6F6F6;
}
.carousel__info h2 {
  grid-row: 1/2;
  grid-column: 2/3;
  align-self: end;
}
.carousel__info p {
  grid-row: 4/5;
  grid-column: 2/3;
  line-height: 1.8;
  font-size: 0.9rem;
  text-align: left;
  font-family: sans-serif;
  width: 80%;
}
.carousel__info .read-more {
  grid-row: 5/6;
  grid-column: 2/3;
  color: #fff;
  text-decoration: none;
  text-align: left;
  font-size: 1.3rem;
  display: flex;
  align-items: center;
  text-transform: capitalize;
  font-family: sans-serif;
  padding: 15px 2px;
  max-width: 250px;
  width: 100%;
}
.carousel__info .read-more:hover svg {
  margin-left: 2rem;
}
.carousel__info .read-more svg {
  fill: #fff;
  margin-left: 1rem;
  width: 20px;
  transition: all 0.1s ease;
}
.carousel__controls {
  grid-column: 2/3;
  grid-row: 2/3;
  display: grid;
  grid-template-columns: 1fr 1fr;
}
.carousel__controls label {
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.3rem;
  text-transform: capitalize;
  color: #A3A3A3;
  cursor: pointer;
}
.carousel__controls label p {
  padding: 0 10px;
}
.carousel__controls label svg {
  width: 20px;
  height: 20px;
  fill: #B0B0B0;
}
.carousel__controls label:first-of-type {
  border: 3px solid #F6F6F6;
}
.carousel__controls label:last-of-type {
  border-bottom: 3px solid #F6F6F6;
}

.section--stories {
  background: linear-gradient(90deg, #F6F6F6 50%, #fff 50%);
  padding: 5rem 0;
}

.stories__container {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-rows: repeat(4, min-content);
  margin: 0 auto;
  grid-gap: 4rem;
  justify-content: center;
  justify-items: center;
  max-width: 1200px;
}
.stories__container h3 {
  grid-column: 1/2;
  grid-row: 1/2;
}
.stories__container input {
  height: 70px;
  width: 150px;
  font-size: 1.3rem;
  text-align: center;
  text-transform: capitalize;
  color: #B0B0B0;
}
.stories__container .stories__select {
  grid-column: 2/3;
  grid-row: 1/2;
}
.stories__container .stories__more {
  grid-row: 4/5;
  grid-column: 2/3;
}

.card {
  display: grid;
}
.card:nth-of-type(1) {
  grid-column: 1/2;
  grid-row: 2/3;
}
.card:nth-of-type(2) {
  grid-column: 2/3;
  grid-row: 2/3;
}
.card:nth-of-type(3) {
  grid-column: 3/4;
  grid-row: 2/3;
}
.card:nth-of-type(4) {
  grid-column: 3/4;
  grid-row: 3/4;
}

.card--big {
  grid-column: 1/3;
  grid-row: 3/4;
}

.section--reel {
  background: #1F1F1F;
  display: grid;
  grid-template-rows: 25px 1fr;
  grid-template-columns: repeat(2, 1fr);
}

.reel__background {
  background: #191919;
  grid-column: 1/2;
  grid-row: 2/3;
}

.reel__container {
  max-width: 1200px;
  margin: 0 auto;
  grid-column: 1/-1;
  grid-row: 2/3;
  z-index: 10;
  padding: 10rem 0;
  width: 100%;
  grid-column-gap: 4rem;
  display: grid;
  grid-template-columns: 1fr 0.5fr;
  grid-template-rows: min-content min-content auto;
}
.reel__container .card--big {
  grid-column: 1/2;
  grid-row: 3/4;
}
.reel__container > h1 {
  grid-column: 1/2;
  grid-row: 2/3;
}
.reel__container > h2 {
  grid-column: 1/2;
  grid-row: 1/2;
}

.reel__side-menu {
  grid-row: 3/4;
  grid-column: 2/3;
  display: grid;
  justify-content: center;
  grid-row-gap: 2rem;
  grid-template-rows: 100px 1fr 100px;
  align-content: center;
}
.reel__side-menu ul {
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  padding: 0;
  margin: 0;
}
.reel__side-menu h2 {
  align-self: end;
}

.section--subscription {
  background: url("8eb9d0aa9a2f8ea4f6aebcc9f6ff3fc8.jpg");
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  box-shadow: inset 0 0 0 1000px rgba(255, 255, 255, 0.9);
  padding: 8rem 0;
}

.subscription__container {
  padding: 5rem 0;
}
.subscription__container h2, .subscription__container h1 {
  text-align: center;
}
.subscription__container h1 {
  color: #2C2C2C;
  font-weight: 300;
  margin-bottom: 5rem;
}
.subscription__container article {
  display: grid;
  grid-template-columns: 20vw 28vw auto 100px;
  grid-template-rows: 20px 1fr 20px;
  align-items: center;
  grid-column-gap: 2rem;
}

.sub__img1 {
  grid-column: 1/2;
  grid-row: 2/3;
}

.sub__img2 {
  grid-column: 2/3;
  grid-row: 2/3;
}

.sub__info {
  grid-column: 3/4;
  grid-row: 2/3;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: repeat(4, auto);
  grid-row-gap: 1rem;
  grid-column-gap: 4rem;
}
.sub__info h4 {
  grid-column: 1/2;
  grid-row: 1/2;
  width: 100%;
  font-size: 1rem;
  text-transform: capitalize;
  font-weight: 900;
  font-family: sans-serif;
  letter-spacing: 1px;
}
.sub__info h3 {
  grid-column: 1/-1;
  grid-row: 2/3;
}
.sub__info p {
  grid-column: 1/-1;
  grid-row: 3/4;
  font-size: 0.9rem;
  font-family: sans-serif;
  margin-bottom: 2rem;
  line-height: 1.9;
}
.sub__info p:first-letter {
  text-transform: capitalize;
}
.sub__info a:first-of-type {
  grid-column: 1/2;
  width: 90%;
}
.sub__info a:not(:first-of-type) {
  grid-column: 2/-1;
  width: 90%;
}

.section--feature {
  display: grid;
  grid-template-columns: 1fr 3fr;
  background: #2C2C2C;
  min-height: 70vh;
}
.section--feature article {
  background: #2C2C2C;
  position: relative;
  top: -70px;
  display: grid;
  grid-template-rows: 70px repeat(6, auto);
  grid-template-columns: 30px 1fr;
  align-items: start;
  grid-row-gap: 1rem;
}
.section--feature article > * {
  grid-column: 2/3;
}
.section--feature article h2 {
  grid-row: 2/3;
}
.section--feature article h1 {
  grid-row: 3/4;
}
.section--feature article span {
  color: #fff;
  grid-row: 5/6;
  font-size: 1.8rem;
}
.section--feature article p {
  grid-row: 6/7;
  width: 90%;
  color: #fff;
  line-height: 2;
  font-size: 0.85rem;
}
.section--feature article a {
  margin-top: 2rem;
  grid-row: 7/8;
}
.section--feature img {
  width: 100%;
  object-fit: cover;
  height: 100%;
}

.footer {
  background: #2C2C2C;
  height: 150px;
}
.card {
  width: 350px;
  justify-content: center;
  justify-items: center;
}
.card img {
  width: 100%;
  object-fit: cover;
}
.card__info {
  display: grid;
  grid-template-columns: min-content 1fr;
  grid-template-rows: repeat(2, auto);
  width: 100%;
  grid-row-gap: 10px;
  grid-column-gap: 1rem;
  padding-top: 1rem;
}
.card__camera {
  grid-column: 1/2;
  grid-row: 1/2;
  justify-self: start;
  width: 30px;
  height: 25px;
  fill: none;
  stroke: #B0B0B0;
  stroke-width: 1px;
}
.card__date {
  grid-column: 2/3;
  grid-row: 1/2;
  display: flex;
  align-items: center;
  justify-content: start;
  font-size: 1.1rem;
  color: #8E8E8E;
  font-weight: 900;
  letter-spacing: 1px;
}
.card a {
  grid-column: 1/-1;
  grid-row: 2/3;
}
.card--big {
  width: 100%;
  display: grid;
  grid-template-rows: auto;
  position: relative;
}
.card--big img {
  object-fit: cover;
  grid-column: 1/-1;
  grid-row: 1/2;
  align-self: start;
  width: 100%;
  height: 100%;
}
.card--big h2 {
  grid-row: 2/3;
  grid-column: 2/3;
  color: #fff;
}
.card--big h1 {
  grid-row: 2/3;
}
.card--big__info {
  grid-column: 1/-1;
  grid-row: 1/2;
  z-index: 15;
  display: grid;
  grid-template-rows: auto auto auto 20px;
  grid-template-columns: 50px 1fr 1fr;
  align-content: end;
  align-items: center;
  justify-items: start;
  color: #F6F6F6;
  background: rgba(0, 0, 0, 0.2);
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  top: 0;
  grid-row-gap: 15px;
}
.card--big__info h1 {
  grid-row: 3/4;
  grid-column: 2/3;
  font-family: serif;
  font-size: 4rem;
  align-self: start;
}
.card--big .btn--arrow {
  grid-column: 3/-1;
  grid-row: 3/4;
}
.card--big__video-svg {
  grid-column: 2/3;
  grid-row: 1/2;
  fill: none;
  stroke: #F6F6F6;
  stroke-width: 2px;
  width: 50px;
  height: 50px;
}
.card--big-simple .btn--arrow {
  grid-column: 2/3;
  justify-content: start;
}

.btn {
  color: #8E8E8E;
  text-decoration: none;
  font-size: 1.3rem;
  height: 60px;
  width: 150px;
  display: flex;
  align-items: center;
  justify-content: center;
  text-transform: capitalize;
  border: 1px solid currentColor;
  font-family: sans-serif;
  letter-spacing: 1px;
}
.btn--black {
  background: #2C2C2C;
  color: #B0B0B0;
}

.btn--arrow {
  width: 100%;
  padding-right: 2rem;
  font-size: 1.1rem;
  font-family: sans-serif;
  text-transform: capitalize;
  text-decoration: none;
  color: #fff;
  letter-spacing: 3px;
  display: flex;
  align-items: center;
}
.btn--arrow svg {
  fill: #F6F6F6;
  margin-left: 1rem;
  width: 20px;
  height: 20px;
}

.btn--arrow-black {
  color: #2C2C2C;
  font-size: 1rem;
}
.btn--arrow-black svg {
  fill: #2C2C2C;
}
